(46, 16, 'Coin_Change.cpp', '#include <climits>\r\n#include <iostream>\r\nusing namespace std;\r\nint findMinCoins(int arr[], int n, int N) {\r\n    int dp[N + 1];\r\n    dp[0] = 0;\r\n    for(int i = 1; i <= N; i++) {\r\n        dp[i] = INT_MAX;\r\n        int res = INT_MAX;\r\n\r\n        for(int c = 0; c < n; c++) {\r\n            if(i - arr[c] >= 0) res = dp[i - arr[c]];\r\n            if(res != INT_MAX) dp[i] = min(dp[i], res + 1);\r\n        }\r\n    }\r\n    return dp[N];\r\n}\r\nint main(){\r\n    int arr[] = {1,2,3,4};\r\n    int n = sizeof(arr) / sizeof(arr[0]);\r\n    int N = 15;\r\n    cout << \"Minimum Number of Coins Required \" << findMinCoins(arr, n, N) << \"\\n\";\r\n    return 0;\r\n}\r\n', '2024-08-30 08:45:21', '2024-08-30 08:45:21'),
(47, 16, 'Bellman Ford', '#include <limits.h>\r\n#include <iostream>\r\n\r\nusing namespace std;\r\n\r\n// Wrapper class for storing an edge\r\nclass Edge {\r\n public:\r\n    int src, dst, weight;\r\n};\r\n\r\n// Wrapper class for storing a graph\r\nclass Graph {\r\n    public:\r\n        int vertexNum, edgeNum;\r\n        Edge *edges;\r\n\r\n        // Constructs a graph with V vertices and E edges\r\n        Graph(int V, int E) {\r\n            this->vertexNum = V;\r\n            this->edgeNum = E;\r\n            this->edges = (Edge *)malloc(E * sizeof(Edge));\r\n        }\r\n\r\n        // Adds the given edge to the graph\r\n        void addEdge(int src, int dst, int weight) {\r\n            static int edgeInd = 0;\r\n            if (edgeInd < this->edgeNum) {\r\n                Edge newEdge;\r\n                newEdge.src = src;\r\n                newEdge.dst = dst;\r\n                newEdge.weight = weight;\r\n                this->edges[edgeInd++] = newEdge;\r\n            }\r\n        }\r\n};\r\n\r\n// Utility function to print distances\r\nvoid print(int dist[], int V) {\r\n    cout << \"\\nVertex  Distance\" << endl;\r\n    for (int i = 0; i < V; i++) {\r\n        if (dist[i] != INT_MAX)\r\n            cout << i << \"\\t\" << dist[i] << endl;\r\n        else\r\n            cout << i << \"\\tINF\" << endl;\r\n    }\r\n}\r\n\r\n// The main function that finds the shortest path from given source\r\n// to all other vertices using Bellman-Ford.It also detects negative\r\n// weight cycle\r\nvoid BellmanFord(Graph graph, int src) {\r\n    int V = graph.vertexNum;\r\n    int E = graph.edgeNum;\r\n    int dist[V];\r\n\r\n    // Initialize distances array as INF for all except source\r\n    // Intialize source as zero\r\n    for (int i = 0; i < V; i++) dist[i] = INT_MAX;\r\n    dist[src] = 0;\r\n\r\n    // Calculate shortest path distance from source to all edges\r\n    // A path can contain maximum (|V|-1) edges\r\n    for (int i = 0; i <= V - 1; i++)\r\n        for (int j = 0; j < E; j++) {\r\n            int u = graph.edges[j].src;\r\n            int v = graph.edges[j].dst;\r\n            int w = graph.edges[j].weight;\r\n\r\n            if (dist[u] != INT_MAX && dist[u] + w < dist[v])\r\n                dist[v] = dist[u] + w;\r\n        }\r\n\r\n    // Iterate inner loop once more to check for negative cycle\r\n    for (int j = 0; j < E; j++) {\r\n        int u = graph.edges[j].src;\r\n        int v = graph.edges[j].dst;\r\n        int w = graph.edges[j].weight;\r\n\r\n        if (dist[u] != INT_MAX && dist[u] + w < dist[v]) {\r\n            cout << \"Graph contains negative weight cycle. Hence, shortest \"\r\n                    \"distance not guaranteed.\"\r\n                 << endl;\r\n            return;\r\n        }\r\n    }\r\n\r\n    print(dist, V);\r\n\r\n    return;\r\n}\r\n\r\n// Driver Function\r\nint main() {\r\n    int V, E, gsrc;\r\n    int src, dst, weight;\r\n    cout << \"Enter number of vertices: \";\r\n    cin >> V;\r\n    cout << \"Enter number of edges: \";\r\n    cin >> E;\r\n    Graph G(V, E);\r\n    for (int i = 0; i < E; i++) {\r\n        cout << \"\\nEdge \" << i + 1 << \"\\nEnter source: \";\r\n        cin >> src;\r\n        cout << \"Enter destination: \";\r\n        cin >> dst;\r\n        cout << \"Enter weight: \";\r\n        cin >> weight;\r\n        G.addEdge(src, dst, weight);\r\n    }\r\n    cout << \"\\nEnter source: \";\r\n    cin >> gsrc;\r\n    BellmanFord(G, gsrc);\r\n\r\n    return 0;\r\n}\r\n', '2024-08-30 09:11:26', '2024-08-30 09:11:26'),
(48, 16, 'Bogo Sort.cpp', '#include <iostream>\r\n#include <algorithm>\r\n#include <array>\r\n#include <cassert>\r\n\r\nnamespace sorting {\r\n    template <typename T, size_t N>\r\n    std::array <T, N> shuffle (std::array <T, N> arr) {\r\n        for (int i = 0; i < N; i++) {\r\n            // Swaps i\'th  index with random index (less than array size)\r\n            std::swap(arr[i], arr[std::rand() % N]);\r\n        }\r\n        return arr;\r\n    }\r\n    template <typename T, size_t N>\r\n    std::array <T, N> randomized_bogosort (std::array <T, N> arr) {\r\n        // Untill array is not sorted\r\n        while (!std::is_sorted(arr.begin(), arr.end())) {\r\n            std::random_shuffle(arr.begin(), arr.end());// Shuffle the array\r\n        }\r\n        return arr;\r\n    }\r\n\r\n}\r\ntemplate <typename T, size_t N>\r\nvoid show_array (const std::array <T, N> &arr) {\r\n    for (int x : arr) {\r\n        std::cout << x << \' \';\r\n    }\r\n    std::cout << \'\\n\';\r\n}\r\nvoid test() {\r\n    // Test 1\r\n    std::array <int, 5> arr1;\r\n    for (int &x : arr1) {\r\n        x = std::rand() % 100;\r\n    }\r\n    std::cout << \"Original Array : \";\r\n    show_array(arr1);\r\n    arr1 = sorting::randomized_bogosort(arr1);\r\n    std::cout << \"Sorted Array : \";\r\n    show_array(arr1);\r\n    assert(std::is_sorted(arr1.begin(), arr1.end()));\r\n    // Test 2\r\n    std::array <int, 5> arr2;\r\n    for (int &x : arr2) {\r\n        x = std::rand() % 100;\r\n    }\r\n    std::cout << \"Original Array : \";\r\n    show_array(arr2);\r\n    arr2 = sorting::randomized_bogosort(arr2);\r\n    std::cout << \"Sorted Array : \";\r\n    show_array(arr2);\r\n    assert(std::is_sorted(arr2.begin(), arr2.end()));\r\n}\r\n\r\n/** Driver Code */\r\nint main() {\r\n    // Testing\r\n    test();\r\n    // Example Usage\r\n    std::array <int, 5> arr = {3, 7, 10, 4, 1}; // Defining array which we want to sort\r\n    std::cout << \"Original Array : \";\r\n    show_array(arr);\r\n    arr = sorting::randomized_bogosort(arr); // Callling bogo sort on it\r\n    std::cout << \"Sorted Array : \";\r\n    show_array(arr); // Printing sorted array\r\n    return 0;\r\n}\r\n', '2024-08-30 09:43:23', '2024-08-30 09:43:23'),
(49, 16, 'FibonacciNumber.cpp', '#include <cassert>\r\n#include <iostream>\r\n\r\nuint64_t fibonacci(uint64_t n) {\r\n    if(n <= 1) return n;\r\n    return fibonacci(n - 1) + fibonacci(n - 2);\r\n}\r\nstatic void test() {\r\n    uint64_t test_case_1 = fibonacci(0);\r\n    assert(test_case_1 == 0);\r\n    std::cout << \"Passed Test 1!\" << std::endl;\r\n\r\n    uint64_t test_case_2 = fibonacci(1);\r\n    assert(test_case_2 == 1);\r\n    std::cout << \"Passed Test 2!\" << std::endl;\r\n\r\n    uint64_t test_case_3 = fibonacci(2);\r\n    assert(test_case_3 == 1);\r\n    std::cout << \"Passed Test 3!\" << std::endl;\r\n\r\n    uint64_t test_case_4 = fibonacci(3);\r\n    assert(test_case_4 == 2);\r\n    std::cout << \"Passed Test 4!\" << std::endl;\r\n\r\n    uint64_t test_case_5 = fibonacci(4);\r\n    assert(test_case_5 == 3);\r\n    std::cout << \"Passed Test 5!\" << std::endl;\r\n\r\n    uint64_t test_case_6 = fibonacci(15);\r\n    assert(test_case_6 == 610);\r\n    std::cout << \"Passed Test 6!\" << std::endl << std::endl;\r\n}\r\nint main() {\r\n    test();\r\n    int n = 0;\r\n    std::cin >> n;\r\n    assert(n >= 0);\r\n    std::cout << \"F(\" << n << \")=\" << fibonacci(n) << std::endl;\r\n}\r\n', '2024-08-30 09:59:36', '2024-08-30 09:59:36'),
(50, 16, 'Generate Parentheses.cpp', '#include <cassert>\r\n#include <iostream>\r\n#include <vector>\r\nnamespace backtracking{\r\n    class generate_parentheses{\r\n        private:\r\n            std::vector<std::string> res;\r\n            void makeStrings(std::string str, int n, int closed, int open);\r\n\r\n        public:\r\n            std::vector<std::string> generate(int n);\r\n    };\r\n\r\n    void generate_parentheses::makeStrings(std::string str, int n, int closed, int open) {\r\n        if(closed > open) return;\r\n        if((str.length() == 2 * n) && (closed != open)) return;\r\n        if(str.length() == 2 * n) {\r\n            res.push_back(str);\r\n            return;\r\n        }\r\n        makeStrings(str + \')\', n, closed + 1, open);\r\n        makeStrings(str + \'(\', n, closed, open + 1);\r\n    }\r\n\r\n    std::vector<std::string> generate_parentheses::generate(int n) {\r\n        backtracking::generate_parentheses::res.clear();\r\n        std::string str = \"(\";\r\n        generate_parentheses::makeStrings(str, n, 0, 1);\r\n        return res;\r\n    }\r\n}\r\n\r\nstatic void test() {\r\n    int n = 0;\r\n    std::vector<std::string> patterns;\r\n    backtracking::generate_parentheses p;\r\n\r\n    n = 1;\r\n    patterns = {{\"()\"}};\r\n    assert(p.generate(n) == patterns);\r\n\r\n    n = 3;\r\n    patterns = {{\"()()()\"}, {\"()(())\"}, {\"(())()\"}, {\"(()())\"}, {\"((()))\"}};\r\n\r\n    assert(p.generate(n) == patterns);\r\n\r\n    n = 4;\r\n    patterns = {{\"()()()()\"}, {\"()()(())\"}, {\"()(())()\"}, {\"()(()())\"},\r\n                {\"()((()))\"}, {\"(())()()\"}, {\"(())(())\"}, {\"(()())()\"},\r\n                {\"(()()())\"}, {\"(()(()))\"}, {\"((()))()\"}, {\"((())())\"},\r\n                {\"((()()))\"}, {\"(((())))\"}};\r\n    assert(p.generate(n) == patterns);\r\n\r\n    std::cout << \"All tests passed\\n\";\r\n}\r\nint main() {\r\n    test();\r\n    return 0;\r\n}\r\n', '2024-08-30 10:09:38', '2024-08-30 10:09:38'),
(51, 16, 'Graph Coloring.cpp', '#include <array>\r\n#include <iostream>\r\n#include <vector>\r\n\r\nnamespace backtracking {\r\n    namespace graph_coloring {\r\n        template <size_t V>\r\n        void printSolution(const std::array<int, V>& color) {\r\n            std::cout << \"Following are the assigned colors\\n\";\r\n            for (auto& col : color) {\r\n                std::cout << col;\r\n            }\r\n            std::cout << \"\\n\";\r\n        }\r\n\r\n        template <size_t V>\r\n        bool isSafe(int v, const std::array<std::array<int, V>, V>& graph, const std::array<int, V>& color, int c) {\r\n            for (int i = 0; i < V; i++) {\r\n                if (graph[v][i] && c == color[i]) {\r\n                    return false;\r\n                }\r\n            }\r\n            return true;\r\n        }\r\n        template <size_t V>\r\n        void graphColoring(const std::array<std::array<int, V>, V>& graph, int m, std::array<int, V> color, int v) {\r\n            if (v == V) {\r\n                printSolution<V>(color);\r\n                return;\r\n            }\r\n            for (int c = 1; c <= m; c++) {\r\n                if (isSafe<V>(v, graph, color, c)) {\r\n                    color[v] = c;\r\n                    graphColoring<V>(graph, m, color, v + 1);\r\n                    color[v] = 0;\r\n                }\r\n            }\r\n        }\r\n    }\r\n}\r\nint main() {\r\n    // Create following graph and test whether it is 3 colorable\r\n    // (3)---(2)\r\n    // |   / |\r\n    // |  /  |\r\n    // | /   |\r\n    // (0)---(1)\r\n\r\n    const int V = 4;\r\n    std::array<std::array<int, V>, V> graph = {\r\n        std::array<int, V>({0, 1, 1, 1}), std::array<int, V>({1, 0, 1, 0}),\r\n        std::array<int, V>({1, 1, 0, 1}), std::array<int, V>({1, 0, 1, 0})};\r\n\r\n    int m = 3;  // Number of colors\r\n    std::array<int, V> color{};\r\n\r\n    backtracking::graph_coloring::graphColoring<V>(graph, m, color, 0);\r\n    return 0;\r\n}\r\n', '2024-08-30 10:31:43', '2024-08-30 10:31:43'),
(52, 16, 'Knight-Tour.cpp', '#include <array>\r\n#include <iostream>\r\n\r\nnamespace backtracking {\r\n    namespace knight_tour {\r\n        template <size_t V>\r\n        bool issafe(int x, int y, const std::array<std::array<int, V>, V> &sol) {\r\n            return (x < V && x >= 0 && y < V && y >= 0 && sol[x][y] == -1);\r\n        }\r\n\r\n        template <size_t V>\r\n        bool solve(int x, int y, int mov, std::array<std::array<int, V>, V> &sol,\r\n                   const std::array<int, V> &xmov, std::array<int, V> &ymov) {\r\n            int k = 0, xnext = 0, ynext = 0;\r\n\r\n            if (mov == V * V) {\r\n                return true;\r\n            }\r\n\r\n            for (k = 0; k < V; k++) {\r\n                xnext = x + xmov[k];\r\n                ynext = y + ymov[k];\r\n\r\n                if (issafe<V>(xnext, ynext, sol)) {\r\n                    sol[xnext][ynext] = mov;\r\n\r\n                    if (solve<V>(xnext, ynext, mov + 1, sol, xmov, ymov) == true) {\r\n                        return true;\r\n                    } else {\r\n                        sol[xnext][ynext] = -1;\r\n                    }\r\n                }\r\n            }\r\n            return false;\r\n        }\r\n    }\r\n}\r\n\r\nint main() {\r\n    const int n = 8;\r\n    std::array<std::array<int, n>, n> sol = {0};\r\n\r\n    int i = 0, j = 0;\r\n    for (i = 0; i < n; i++) {\r\n        for (j = 0; j < n; j++) {\r\n            sol[i][j] = -1;\r\n        }\r\n    }\r\n\r\n    std::array<int, n> xmov = {2, 1, -1, -2, -2, -1, 1, 2};\r\n    std::array<int, n> ymov = {1, 2, 2, 1, -1, -2, -2, -1};\r\n\r\n    sol[0][0] = 0;\r\n\r\n    bool flag = backtracking::knight_tour::solve<n>(0, 0, 1, sol, xmov, ymov);\r\n    if (flag == false) {\r\n        std::cout << \"Error: Solution does not exist\\n\";\r\n    } else {\r\n        for (i = 0; i < n; i++) {\r\n            for (j = 0; j < n; j++) {\r\n                std::cout << sol[i][j] << \"  \";\r\n            }\r\n            std::cout << \"\\n\";\r\n        }\r\n    }\r\n    return 0;\r\n}\r\n', '2024-08-31 02:48:10', '2024-08-31 02:48:10'),
(53, 16, 'Magic_Senquence.cpp', '#include <algorithm>\r\n#include <cassert>\r\n#include <iostream>\r\n#include <list>\r\n#include <numeric>\r\n#include <vector>\r\nnamespace backtracking{\r\n    namespace magic_sequence {\r\n        using sequence_t = std::vector<unsigned int>;\r\n\r\n        void print(const sequence_t& s){\r\n            for(const auto& item : s) std::cout << item << \" \";\r\n            std::cout << std::endl;\r\n        }\r\n\r\n        bool is_magic(const sequence_t& s) {\r\n            for(unsigned int i = 0; i < s.size(); i++) {\r\n                if(std::count(s.cbegin(), s.cend(), i) != s[i]) {\r\n                    return false;\r\n                }\r\n            }\r\n            return true;\r\n        }\r\n\r\n        bool filtering(const sequence_t& s, unsigned int depth){\r\n            return std::accumulate(s.cbegin(), s.cbegin() + depth, static_cast<unsigned int>(0)) <= s.size();\r\n        }\r\n\r\n        void solve(sequence_t* s, std::list<sequence_t>* ret, unsigned int depth = 0) {\r\n            if(depth == s->size()) {\r\n                if(is_magic(*s)) {\r\n                    ret->push_back(*s);\r\n                }\r\n            } else {\r\n                for(unsigned int i = 0; i < s->size(); i++) {\r\n                    (*s)[depth] = i;\r\n                    if(filtering(*s, depth + 1)) {\r\n                        solve(s, ret, depth + 1);\r\n                    }\r\n                }\r\n            }\r\n        }\r\n    }\r\n}\r\n\r\nstatic void test() {\r\n    backtracking::magic_sequence::sequence_t s_magic = {6, 2, 1, 0, 0, 0, 1, 0, 0, 0};\r\n    assert(backtracking::magic_sequence::is_magic(s_magic));\r\n    backtracking::magic_sequence::sequence_t s_not_magic = {5, 2, 1, 0, 0, 0, 1, 0, 0, 0};\r\n    assert(!backtracking::magic_sequence::is_magic(s_not_magic));\r\n}\r\n\r\nint main() {\r\n    test();\r\n    for(unsigned int i = 2; i < 12; i++) {\r\n        std::cout << \"solution for n = \" << i << std::endl;\r\n        std::list<backtracking::magic_sequence::sequence_t> list_of_solutions;\r\n        backtracking::magic_sequence::sequence_t s1(i, i);\r\n        backtracking::magic_sequence::solve(&s1, &list_of_solutions);\r\n        for(const auto& item : list_of_solutions) {\r\n            backtracking::magic_sequence::print(item);\r\n        }\r\n    }\r\n    return 0;\r\n}\r\n', '2024-08-31 03:28:29', '2024-08-31 03:28:29'),
(54, 16, 'Minimax.cpp', '#include <algorithm>\r\n#include <array>\r\n#include <cmath>\r\n#include <iostream>\r\nnamespace backtracking{\r\n    template <size_t T>\r\n    int minimax(int depth, int node_index, bool is_max, const std::array<int, T> &scores, double height) {\r\n        if(depth == height) {\r\n            return scores[node_index];\r\n        }\r\n        int v1 = minimax(depth + 1, node_index * 2, !is_max, scores, height);\r\n        int v2 = minimax(depth + 1, node_index * 2 + 1, !is_max, scores, height);\r\n        return is_max ? std::max(v1, v2) : std::min(v1, v2);\r\n    }\r\n}\r\n\r\nint main() {\r\n    std::array<int, 8> scores = {90, 23, 6, 33, 21, 65, 123, 34423};\r\n    double height = log2(scores.size());\r\n    std::cout << \"optimal value: \" << backtracking::minimax(0, 0, true, scores, height) << std::endl;\r\n    return 0;\r\n}\r\n', '2024-08-31 03:36:02', '2024-08-31 03:36:02'),
(55, 16, 'N Queens.cpp', '#include <array>\r\n#include <iostream>\r\nnamespace backtracking{\r\n    namespace n_queens{\r\n        template <size_t n>\r\n        void printSolution(const std::array<std::array<int, n>, n> &board) {\r\n            std::cout << \"\\n\";\r\n            for(int i = 0; i < n; i++) {\r\n                for(int j = 0; j < n; j++) {\r\n                    std::cout << \"\" << board[i][j] << \" \";\r\n                }\r\n                std::cout << \"\\n\";\r\n            }\r\n        }\r\n\r\n        template <size_t n>\r\n        bool isSafe(const std::array<std::array<int, n>, n> &board, const int &row, const int &col) {\r\n            int i = 0; j = 0;\r\n            for(i = 0; i < col; i++) {\r\n                if(board[row][i]){\r\n                    return false;\r\n                }\r\n            }\r\n            for(i = row, j = col; i >= 0 && j >= 0; i--, j--) {\r\n                if(board[i][j]) return false;\r\n            }\r\n            for(i = row, j = col; j >= 0 && i < n; i++, j--) {\r\n                if(board[i][j]) {\r\n                    return false;\r\n                }\r\n            }\r\n            return true;\r\n        }\r\n\r\n        template <size_t n>\r\n        void solveNQ(std::array<std::array<int, n>, n> board, const int &col) {\r\n            if(col >= n) {\r\n                printSolution<n>(board);\r\n                return;\r\n            }\r\n            for(int i = 0; i < n; i++) {\r\n                if(isSafe<n>(board, i, col)) {\r\n                    board[i][col] = 1;\r\n                    solveNQ<n>(board, col + 1);\r\n                    board[i][col] = 0;\r\n                }\r\n            }\r\n        }\r\n    }\r\n}\r\n\r\nint main() {\r\n    const int n = 4;\r\n    std::array<std::array<int, n>, n> board = {\r\n        std::array<int, n>({0,0,0,0}), std::array<int, n>({0,0,0,0}),\r\n        std::array<int, n>({0,0,0,0}), std::array<int, n>({0,0,0,0});\r\n    }\r\n    backtracking::n_queens::solveNQ<n>(board, 0);\r\n    return 0;\r\n}\r\n', '2024-08-31 04:01:00', '2024-08-31 04:01:00'),
(56, 16, 'N_Queen_All.cpp', '#include<array>\r\n#include<iostream>\r\nnamespace backtracking{\r\n    namespace n_queens_optimized{\r\n        template <size_t n>\r\n        void PrintSol(const std::array<std::array<int, n>, n> &board) {\r\n            for(int i = 0; i < n; i++) {\r\n                for(int j = 0; j < n; j+=) {\r\n                    std::cout << board[i][j] << \" \";\r\n                }\r\n                std::cout << std::endl;\r\n            }\r\n            std::cout << std::endl;\r\n            if(n % 2 == 0 || (n % 2 == 1 && board[n / 2 + 1][0] != 1)) {\r\n                for(int i = 0; i < n; i++) {\r\n                    for(int j = 0; j < n; j++) {\r\n                        std::cout << board[j][i] << \" \";\r\n                    }\r\n                    std::cout << std::endl;\r\n                }\r\n                std::cout << std::endl;\r\n            }\r\n        }\r\n\r\n        template <size_t n>\r\n        bool CanIMove(const std::array<std::array<int, n>, n> &board, int row, int col) {\r\n            for(int i = 0; i <= col; i++) {\r\n                if(board[row][i] == 1) return false;\r\n            }\r\n            for(int i = row, j = col; i >= 0 && j >= 0; i--, j--) {\r\n                if(board[i][j] == 1) return false;\r\n            }\r\n            for(int i = row, j = col; i <= n - 1 && j >= 0; i++, j--) {\r\n                if(board[i][j] == 1) return false;\r\n            }\r\n            return true;\r\n        }\r\n\r\n        template <size_t n>\r\n        void NQueenSol(std::array<std::array<int, n>, n> board, int col) {\r\n            if(col >= n) {\r\n                PrintSol<n>(board);\r\n                return;\r\n            }\r\n            for(int i = 0; i < n; i++) {\r\n                if(CanIMove<n>(board, i, col)) {\r\n                    board[i][col] = 1;\r\n                    NQueenSol<n>(board, col + 1);\r\n                    board[i][col] = 0;\r\n                }\r\n            }\r\n        }\r\n    }\r\n}\r\n\r\nint main() {\r\n    const int n = 4;\r\n    std::array<std::array<int, n>, n> board{};\r\n    if(n % 2 == 0) {\r\n        for(int i = 0; i <= n / 2 - 1; i++) {\r\n            if(backtracking::n_queens_optimized::CanIMove(board, i, 0)) {\r\n                board[i][0] = 1;\r\n                backtracking::n_queens_optimized::NQueenSol(board, 1);\r\n                board[i][0] = 0;\r\n            }\r\n        }\r\n    } else {\r\n        for(int i = 0; i <= n / 2; i++) {\r\n            if(backtracking::n_queens_optimized::CanIMove(board, i, 0)) {\r\n                board[i][0] = 1;\r\n                backtracking::n_queens_optimized::NQueenSol(board, 1);\r\n                board[i][0] = 0;\r\n            }\r\n        }\r\n    }\r\n    return 0;\r\n}\r\n', '2024-08-31 04:52:12', '2024-08-31 04:52:12'),
